import React from 'react'

const Header = (props) => {
  return (
    <div>
      <h1>{props.coursename}</h1>
    </div>
  )
}

const Part = (props) => {
  return(
    <div>
      <p>{props.name} {props.exercise}</p>
    </div>
  )
}

const Content = (props) => {
  return (
    <div>
      <Part name={props.part1name} exercise={props.part1ex}/>
      <Part name={props.part2name} exercise={props.part2ex}/>
      <Part name={props.part3name} exercise={props.part3ex}/>
    </div>
  )
}

const Total = (props) => {
  return (
    <div>
      <p>Number of exercises {props.part1ex + props.part2ex + props.part3ex}</p>
    </div>
  )
}

const App = () => {
  const course = 'Half stack application development'
  const part1 =  {
    name: 'Fundamentals of React',
    exercises: 10
  }

  const part2 = {
    name: 'Using props to pass data',
    exercises: 7
  }

  const part3 = {
    name: 'State of a component',
    exercises: 14
  }

  return (
    <div>
      <Header coursename={course}/>
      <Content part1name={part1.name} part1ex={part1.exercises} part2name={part2.name} part2ex={part2.exercises} part3name={part3.name} part3ex={part3.exercises}/>
      <Total part1ex={part1.exercises} part2ex={part2.exercises} part3ex={part3.exercises}/>
    </div>
  )
}

export default App
